from PIL import Image, ImageFilterimport globimage_size = (30, 40)background = Image.open("/Users/mateuszochal/Documents/University/RoboSoc/ShapeRecognition/Images/Original/background-original.jpg")width, height = background.sizecount = 0step = 40h = 0w = 0height_b = 200width_b = 150while h + height_b < height:    w = 0    while w + width_b < width:        temp = background.crop((w, h, w + width_b, h + height_b))        temp = temp.convert('LA')        temp.thumbnail(image_size, Image.ANTIALIAS)        #temp.show()        temp.save("/Users/mateuszochal/Documents/University/RoboSoc/ShapeRecognition/Images/Transformed/Background/img" + str(count) + ".png")        count += 1        w += step    h += stepfor backgroundfilename in glob.glob("/Users/mateuszochal/Documents/University/RoboSoc/ShapeRecognition/Images/Original/Background/*.jpeg"):    background = Image.open(backgroundfilename)    if background.size[0] * 4 / 3 > background.size[1]:        size = ('auto', height_b)    else:        size = (width_b, 'auto')    background.thumbnail(size, Image.ANTIALIAS)    if background.size[0] > width_b or background.size[1] > height_b:        background = background.crop((0, 0, width_b, height_b))    temp = background.convert('LA')    temp.thumbnail(image_size, Image.ANTIALIAS)    #temp.show()    temp.save("/Users/mateuszochal/Documents/University/RoboSoc/ShapeRecognition/Images/Transformed/Background/img" + str(count) + ".png")    count += 1