from PIL import Image, ImageFilterimport globimage_size = (30, 40)count = 0width_b = 150height_b = 200width_f = 100height_f = 150for backgroundfilename in glob.glob("/Users/mateuszochal/Documents/University/RoboSoc/ShapeRecognition/Images/Original/Background/*.jpeg"):    for foregroundfilename in glob.glob("/Users/mateuszochal/Documents/University/RoboSoc/ShapeRecognition/Images/Original/Cylinders/*.png"):        foreground = Image.open(foregroundfilename)        background = Image.open(backgroundfilename)        w, h = foreground.size        x = int(w/2)        y = int(h/2)        foreground = foreground.crop((x-100, y-150, x+100, y+150))        foreground.thumbnail((width_f,height_f), Image.ANTIALIAS)        if background.size[0] * 4 / 3 > background.size[1]:            size = ('auto', height_b)        else:            size = (width_b, 'auto')        background.thumbnail(size, Image.ANTIALIAS)        if background.size[0] > width_b or background.size[1] > height_b:            background = background.crop((0, 0, width_b, height_b))        for i in range(3):            for j in range(3):                temp = background.copy()                temp.paste(foreground, ((i-1)*10 + 25, (j-1)*10 + 25), foreground)                temp = temp.convert('LA')                temp.thumbnail(image_size, Image.ANTIALIAS)                #temp.show()                temp.save("/Users/mateuszochal/Documents/University/RoboSoc/ShapeRecognition/Images/Transformed/Cylinders/img" + str(count) + ".png")                count += 1